import React, { useState } from 'react';
import './ExpenseForm.css';

function ExpenseForm(props) {
    const [enteredTtile, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    const titleChangeHandler = (e) => {
        console.log(e.target.value.length);
        setEnteredTitle(e.target.value);
    };
    const amountChangeHandler = (e) => {
        setEnteredAmount(e.target.value);
    };
    const dateChangeHandler = (e) => {
        setEnteredDate(e.target.value);
    };

    const submitHandler = (e) => {
        e.preventDefault();
        const expenseData = {
            title: enteredTtile,
            amount: enteredAmount,
            date: new Date(enteredDate),
        };
        props.onSaveExpenseData(expenseData);
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    };
    const [content, setContent] = useState(
        <div className="new-expense__controls">
            <div className="new-expense__control">
                Â <label>Title</label>
                <input type="text" value={enteredTtile} onChange={titleChangeHandler} />
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" value={enteredAmount} onChange={amountChangeHandler} />
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2023-12-31" value={enteredDate} onChange={dateChangeHandler} />
            </div>
        </div>
    );
    function buttonHandler() {
        setContent('Add Expense');
    }
    return (
        <form onSubmit={submitHandler}>
            <div className="new-expense__actions">
                <button onClick={buttonHandler}>Cancel</button>
                <button type="submit" onClick={buttonHandler}>
                    Add Expense
                </button>
            </div>
        </form>
    );
}
export default ExpenseForm;
